        private void THREAD_SIZE_TextChanged(object sender, EventArgs e)
        {
            if (System.Text.RegularExpressions.Regex.IsMatch(THREAD_SIZE.Text, "[^0-9]"))
            {
                MessageBox.Show("Please enter only numbers.");
                THREAD_SIZE.Text = THREAD_SIZE.Text.Remove(THREAD_SIZE.Text.Length - 1);
            }
            try
            {
                ulong TEXT = Convert.ToUInt64(THREAD_SIZE.Text);
                if (!(65535 > TEXT && 0 < TEXT))
                {
                    MessageBox.Show("Please enter integers 0 between 65535");
                    THREAD_SIZE.Text = THREAD_SIZE.Text.Remove(THREAD_SIZE.Text.Length - THREAD_SIZE.Text.Length);
                }
            }
            catch (FormatException)
            {
            }
        }

